mapper.select:
select uid, username, userid, email
from ssafy_member
where userid = #{userid} and userpwd = #{userpwd}

select uid, username, userid, email, joindate
from ssafy_member
where userid = #{userid}

select token
from ssafy_member
where userid = #{userid}

mapper.update:
update ssafy_member
set token = #{token}
where userid = #{userid}

update ssafy_member
set token = #{token, jdbcType=VARCHAR}
where userid = #{userid}

update ssafy_member
set username = #{username}, email = #{email}, userpwd = #{userpwd}
where uid = #{uid}
